import { CommonModule } from '@angular/common';
import { Component, Input, OnInit } from '@angular/core';
import { Diagnostic } from './../../../shared/models/Dpi/Diagnostic';
import { Consultation } from '../../../shared/models/Dpi/Consultation';
import { Medicament } from './../../../shared/models/Dpi/Medicament';
import { Ordonnance } from './../../../shared/models/Dpi/Ordonnance';
import { ConsultationService } from '../../../services/DossierPatient/Consultation/consultation.service';
import { FormsModule } from '@angular/forms';
import { jsPDF } from 'jspdf';
import { MedecinService } from '../../../services/medecin/medecin.service';

@Component({
  selector: 'app-diagnostic',
  templateUrl: './diagnostic.component.html',
  styleUrls: ['./diagnostic.component.css'],
  imports: [CommonModule, FormsModule]
})
export class DiagnosticComponent implements OnInit {
  @Input() diagnostic: Diagnostic | null = null;
  @Input() consultation: Consultation | null = null;


  hospitalName: string = 'HOPITAL ZERALDA';
  doctorName: string = 'AYA LAKACHE';
  address: string = 'ALGER ';
  phone: string = '06678765448';

  ordonnance: Ordonnance | null = null;
  medicaments: Medicament[] = [];
  editMode: boolean = false;
  editMedicamentMode: boolean = false;
  selectedMedicament: any = null;
  isAddingMedicament = false;

  temp: Ordonnance = {
    id: '',
    diagnosticId: '',
    duree: '',
    isValid: false
  };

  constructor(private consultationService: ConsultationService,
    private medecinService: MedecinService ) { }

  ngOnInit(): void {
    if (this.diagnostic) {
      this.loadOrdonnance();
      this.loadMedicaments();
    }
  }

  //  a mettre dans le service ( on recupere lordonnance )
  loadOrdonnance(): void {
    if (this.diagnostic?.ordonnanceId) {
      this.ordonnance = this.consultationService.Ordonnances.find(
        ord => ord.id === this.diagnostic?.ordonnanceId
      ) || null;
    }
  }


  // meme chose il faut la mettre dans le service ( on recupere les medicaments asscoies a cette ordonnance)
  loadMedicaments(): void {
    if (this.ordonnance) {
      this.medicaments = this.consultationService.Medicamenta.filter(
        med => med.ordonnanceId === this.ordonnance!.id
      );
    }
  }

  toggleEditMode(): void {
    this.editMode = !this.editMode;

    if (!this.ordonnance) {
      const newId = `ORD${Math.floor(Math.random() * 100000)}`;
      this.ordonnance = {
        id: newId,
        duree: '',
        isValid: false,
        diagnosticId: this.diagnostic?.id || ''
      };
      if (this.diagnostic) {
        this.diagnostic.ordonnanceId = newId;
      }
    }

    if (this.editMode && this.ordonnance) {
      this.temp = { ...this.ordonnance };
    }
  }

  cancelEdit(): void {
    this.editMode = false;
    this.temp = { id: '', diagnosticId: '', duree: '', isValid: false }; // Réinitialise temp
  }

  // il faut la mettre dans le service aussi + la varialble doit etre modifiee globalement
  saveChanges(): void {
    if (this.ordonnance) {
      this.ordonnance.duree = this.temp.duree;
      this.ordonnance.isValid = this.temp.isValid;
      this.editMode = false;
    }
  }

  ajouterMedicament() {
    this.selectedMedicament = {
      id: null,
      nom: '',
      duree: '',
      dose: '',
      dosePrise: '',
      dosePrevues: '',
    };
    this.isAddingMedicament = true;
    this.editMedicamentMode = true;
  }
  telechargerOrdonnace(): void {
    if (!this.ordonnance || !this.ordonnance.duree) {
      console.error('L\'ordonnance n\'a pas de durée définie.');
      return;
  }
  const imageUrl ="data:image/jpeg;base64,"
  const doc = new jsPDF();

  // Variables dynamiques à récupérer

  const hospitalName = this.hospitalName || "Hôpital Inconnu";
  const doctorName = this.doctorName || "Dr. Inconnu";
  const address = this.address || "Adresse non définie";
  const phone = this.phone || "Téléphone non défini";

  // Calcul de la largeur et hauteur de la page pour centrer le texte
  const pageWidth = doc.internal.pageSize.getWidth();
  const pageHeight = doc.internal.pageSize.getHeight();

  // En-tête de l'ordonnance
  doc.setFillColor(5, 104, 94); // Couleur d'arrière-plan
  doc.rect(0, 0, 210, 60, 'F'); // Rectangle pour l'en-tête
  doc.setFontSize(18);
  doc.setFont('helvetica'); // Texte en gras
  doc.setTextColor(255, 255, 255); // Texte blanc
  doc.text(hospitalName, pageWidth / 2, 25, { align: 'center' }); // Texte centré
  doc.setFontSize(14);
  doc.text(`Dr. ${doctorName}`, pageWidth / 2, 35, { align: 'center' });

  // Section des informations du patient et de l'ordonnance
   // Couleur d'arrière-plan
  //doc.rect(0,60 , 210, 1000, "F"); // Rectangle d'arrière-plan

  doc.setFontSize(20);
  doc.setFont('helvetica'); // Texte en gras
  doc.setTextColor(5, 104, 94); // Définir la couleur du texte (RGB : #05685E)

  doc.addImage(imageUrl, 'PNG', 0, 0, 60, 60); // (image, format, x, y, largeur, hauteur)

  doc.text('Ordonnance', pageWidth / 2, 80, { align: 'center' }); // Titre centré
  doc.setFontSize(16);
  doc.setFont('helvetica'); // Texte en gras
  doc.setTextColor(0, 0, 0); // Définir la couleur du texte (RGB : #05685E)

  //doc.line(10, 85, 200, 85); // Ligne de séparation
  doc.text(`Durée:     ${this.ordonnance.duree}`, 10, 95);
  doc.text(`Validité:  ${this.ordonnance.isValid ? 'Valide' : 'Non valide'}`, 10, 105);

  // Médicaments
  doc.setFontSize(18); // Agrandir pour la section médicaments
  doc.setFont('helvetica'); // Définir la police en gras
  doc.setTextColor(5, 104, 94); // Définir la couleur du texte (RGB : #05685E)

  doc.text('Médicaments:', pageWidth/2, 120,{ align: 'center' });
  //doc.line(70, 122, 140, 122); // La ligne est tracée à 2 unités sous le texte

  let yPosition = 140;
  this.medicaments.forEach((med, index) => {
    doc.setTextColor(0,0,0); // Définir la couleur du texte (RGB : #05685E)

      doc.setFont('helvetica'); // Médicaments en gras
      doc.text(`${index + 1}. ${med.nom} - Dose: ${med.dose}`, 10, yPosition);
      yPosition += 10;
      if (yPosition > 270) { // Nouvelle page si dépassement
          doc.addPage();
          yPosition = 20;
      }
  }); //Pied de page avec texte centré et taille augmentée
  doc.setFontSize(11);
  doc.setFont('helvetica');
  doc.text(`Adresse: ${address}`, 2*pageWidth / 3, pageHeight - 30 );
  doc.text(`Téléphone: ${phone}`, 2*pageWidth / 3, pageHeight - 25 );
  doc.text('Signature:________', 2*pageWidth / 3, pageHeight - 20 );

  // Enregistrer le fichier PDF
  doc.save('ordonnance.pdf');


  }
  // service
  supprimer(medicamentId: string): void {
    this.medicaments = this.medicaments.filter(med => med.id !== medicamentId);
  }

  //service
  modifierMedicament(medicament: any) {
    this.selectedMedicament = { ...medicament };
    this.isAddingMedicament = false;
    this.editMedicamentMode = true;
  }
  cancelMedicamentEdit(): void {
    this.editMedicamentMode = false;
    this.selectedMedicament = null;
    this.isAddingMedicament = false;
  }
  //SERVICE
  saveMedicamentChanges() {
    if (this.isAddingMedicament) {
      const newId = `MED$${Math.floor(Math.random() * 100000)}`;
      this.selectedMedicament.id = newId;
      this.medicaments.push(this.selectedMedicament);
    } else {
      const index = this.medicaments.findIndex(
        (med) => med.id === this.selectedMedicament.id
      );
      if (index > -1) {
        this.medicaments[index] = { ...this.selectedMedicament };
      }
    }

    this.cancelMedicamentEdit();
  }
}
